import React, { useState } from 'react';

// --- ÏïÑÏù¥ÏΩò Ïª¥Ìè¨ÎÑåÌä∏Îì§ (Î≥ÄÍ≤Ω ÏóÜÏùå) ---
const BotIcon = (props) => (
  <svg {...props} xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M12 8V4H8" /><rect width="16" height="12" x="4" y="8" rx="2" /><path d="M2 14h2" /><path d="M20 14h2" /><path d="M15 13v2" /><path d="M9 13v2" /></svg>
);
const UserIcon = (props) => (
  <svg {...props} xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M19 21v-2a4 4 0 0 0-4-4H9a4 4 0 0 0-4 4v2" /><circle cx="12" cy="7" r="4" /></svg>
);
const CpuIcon = (props) => (
    <svg {...props} xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><rect x="4" y="4" width="16" height="16" rx="2" /><rect x="9" y="9" width="6" height="6" /><path d="M15 2v2" /><path d="M15 20v2" /><path d="M2 15h2" /><path d="M2 9h2" /><path d="M20 15h2" /><path d="M20 9h2" /><path d="M9 2v2" /><path d="M9 20v2" /></svg>
);

// --- Ïû¨ÏÇ¨Ïö© UI Ïª¥Ìè¨ÎÑåÌä∏Îì§ (Î≥ÄÍ≤Ω ÏóÜÏùå) ---
const GeminiRoleCard = ({ title, description, features }) => {
  return (
    <div className="bg-white p-6 rounded-2xl shadow-lg border border-gray-100 h-full flex flex-col">
      <div className="flex items-center mb-4">
        <div className="bg-indigo-100 text-indigo-600 p-3 rounded-full mr-4">
          <CpuIcon className="w-8 h-8" />
        </div>
        <div>
          <h3 className="text-xl font-bold text-gray-800">{title}</h3>
          <p className="text-gray-500 text-sm">{description}</p>
        </div>
      </div>
      <ul className="space-y-3 text-gray-700 mt-2 list-inside list-disc pl-2 flex-grow">
        {features.map((feature, index) => (
          <li key={index} className="leading-relaxed">{feature}</li>
        ))}
      </ul>
    </div>
  );
};

const WorkflowStep = ({ number, title, assignee, description }) => {
  const isAI = assignee === 'Gemini';
  const bgColor = isAI ? 'bg-indigo-50' : 'bg-gray-50';
  const textColor = isAI ? 'text-indigo-700' : 'text-gray-700';
  const badgeBgColor = isAI ? 'bg-indigo-200' : 'bg-gray-200';
  const badgeTextColor = isAI ? 'text-indigo-800' : 'text-gray-800';

  return (
    <li className={`flex items-start p-4 rounded-xl ${bgColor} border ${isAI ? 'border-indigo-100' : 'border-gray-100'}`}>
      <div className={`flex items-center justify-center w-12 h-12 rounded-full mr-4 ${textColor} ${isAI ? 'bg-indigo-100' : 'bg-gray-100'} font-bold text-lg flex-shrink-0`}>
        {number}
      </div>
      <div className="flex-grow">
        <div className="flex items-center justify-between mb-1">
          <h4 className={`font-bold ${textColor}`}>{title}</h4>
          <span className={`px-3 py-1 text-xs font-semibold rounded-full ${badgeBgColor} ${badgeTextColor} flex items-center`}>
            {isAI ? <BotIcon className="w-4 h-4 mr-1.5" /> : <UserIcon className="w-4 h-4 mr-1.5" />}
            {isAI ? 'AI ÏûêÎèôÌôî' : 'ÏÇ¨Ïö©Ïûê ÏàòÌñâ'}
          </span>
        </div>
        <p className="text-sm text-gray-600">{description}</p>
      </div>
    </li>
  );
};


// --- Î©îÏù∏ Ïï± Ïª¥Ìè¨ÎÑåÌä∏ ---
export default function App() {
  const [taskInput, setTaskInput] = useState("Î∂ÑÍ∏∞Î≥Ñ Ïã§Ï†Å Î≥¥Í≥†ÏÑú ÏûëÏÑ±");
  const [processSteps, setProcessSteps] = useState("");
  const [analysisResult, setAnalysisResult] = useState(null);
  const [isGenerating, setIsGenerating] = useState(false);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [error, setError] = useState(null);

  const apiKey = ""; // API ÌÇ§Îäî Canvas ÌôòÍ≤ΩÏóêÏÑú ÏûêÎèôÏúºÎ°ú Ï†úÍ≥µÎê©ÎãàÎã§.
  const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;

  // 1Îã®Í≥Ñ: ÏóÖÎ¨¥ -> ÏÑ∏Î∂Ä ÌîÑÎ°úÏÑ∏Ïä§ ÏÉùÏÑ±
  const handleGenerateProcess = async () => {
    if (!taskInput.trim()) {
      setError("Î∂ÑÏÑùÌï† ÏóÖÎ¨¥Î•º ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî.");
      return;
    }
    setIsGenerating(true);
    setError(null);
    setProcessSteps("");
    setAnalysisResult(null);

    const systemPrompt = `ÎãπÏã†ÏùÄ Ï†ÑÎ¨∏ ÌîÑÎ°úÏ†ùÌä∏ Îß§ÎãàÏ†ÄÏûÖÎãàÎã§. ÏÇ¨Ïö©ÏûêÍ∞Ä ÏûÖÎ†•Ìïú ÏóÖÎ¨¥Î•º ÏùºÎ∞òÏ†ÅÏù∏ ÏàòÌñâ Îã®Í≥ÑÎ°ú ÎÇòÎàÑÏñ¥, Í∞Å Îã®Í≥ÑÎ•º Ìïú Ï§ÑÏóê ÌïòÎÇòÏî© ÎÇòÏó¥Ìï¥Ï£ºÏÑ∏Ïöî. Î≤àÌò∏ÎÇò ÌäπÏàòÎ¨∏ÏûêÎäî Î∂ôÏù¥ÏßÄ ÎßàÏÑ∏Ïöî.`;
    const payload = {
      contents: [{ parts: [{ text: `ÏóÖÎ¨¥: ${taskInput}` }] }],
      systemInstruction: { parts: [{ text: systemPrompt }] },
    };

    try {
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      if (!response.ok) throw new Error(`API ÏöîÏ≤≠ Ïã§Ìå®: ${response.statusText}`);
      const result = await response.json();
      const generatedText = result.candidates?.[0]?.content?.parts?.[0]?.text;
      if (generatedText) {
        setProcessSteps(generatedText);
      } else {
        throw new Error("APIÍ∞Ä Ïú†Ìö®Ìïú ÌîÑÎ°úÏÑ∏Ïä§Î•º ÏÉùÏÑ±ÌïòÏßÄ Î™ªÌñàÏäµÎãàÎã§.");
      }
    } catch (err) {
      console.error(err);
      setError("ÌîÑÎ°úÏÑ∏Ïä§ ÏÉùÏÑ± Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    } finally {
      setIsGenerating(false);
    }
  };

  // 2Îã®Í≥Ñ: ÏÑ∏Î∂Ä ÌîÑÎ°úÏÑ∏Ïä§ -> ÎåÄÏãúÎ≥¥Îìú Î∂ÑÏÑù
  const handleAnalyze = async () => {
    if (!processSteps.trim()) {
      setError("Î∂ÑÏÑùÌï† ÌîÑÎ°úÏÑ∏Ïä§ ÎÇ¥Ïö©Ïù¥ ÏóÜÏäµÎãàÎã§. Î®ºÏ†Ä ÌîÑÎ°úÏÑ∏Ïä§Î•º ÏÉùÏÑ±Ìï¥ Ï£ºÏÑ∏Ïöî.");
      return;
    }
    setIsAnalyzing(true);
    setError(null);
    setAnalysisResult(null);

    const systemPrompt = `ÎãπÏã†ÏùÄ ÎπÑÏ¶àÎãàÏä§ ÌîÑÎ°úÏÑ∏Ïä§ ÏûêÎèôÌôî Ï†ÑÎ¨∏ Ïª®ÏÑ§ÌÑ¥Ìä∏ÏûÖÎãàÎã§. ÏÇ¨Ïö©ÏûêÍ∞Ä ÏûÖÎ†•Ìïú ÏóÖÎ¨¥ ÌîÑÎ°úÏÑ∏Ïä§Ïùò Í∞Å Îã®Í≥ÑÎ•º Î∂ÑÏÑùÌïòÏó¨, Ïñ¥Îñ§ Î∂ÄÎ∂ÑÏùÑ Gemini Í∞ôÏùÄ AIÍ∞Ä ÏûêÎèôÌôîÌï† Ïàò ÏûàÍ≥† Ïñ¥Îñ§ Î∂ÄÎ∂ÑÏùÑ ÏÇ¨ÎûåÏù¥ ÏßÅÏ†ë ÏàòÌñâÌï¥Ïïº ÌïòÎäîÏßÄ Íµ¨Î∂ÑÌï¥Ïïº Ìï©ÎãàÎã§. Î∂ÑÏÑù Í≤∞Í≥ºÎ•º Î∞òÎìúÏãú ÏïÑÎûòÏùò JSON Ïä§ÌÇ§ÎßàÏóê ÎßûÏ∂∞ ÏùëÎãµÌï¥Ï£ºÏÑ∏Ïöî. 'assignee' ÌïÑÎìúÎäî 'Gemini' ÎòêÎäî 'User' Í∞íÎßå Í∞ÄÏßà Ïàò ÏûàÏäµÎãàÎã§. 'geminiRoles'ÏóêÎäî AIÍ∞Ä ÏàòÌñâÌï† Ïàò ÏûàÎäî Ïó≠Ìï†Îì§ÏùÑ ÏöîÏïΩÌïòÏó¨ Îã¥ÏïÑÏ£ºÏÑ∏Ïöî.`;
    const payload = {
      contents: [{ parts: [{ text: `Î∂ÑÏÑùÌï† ÏóÖÎ¨¥ ÌîÑÎ°úÏÑ∏Ïä§:\n${processSteps}` }] }],
      systemInstruction: { parts: [{ text: systemPrompt }] },
      generationConfig: {
        responseMimeType: "application/json",
        responseSchema: {
          type: "OBJECT",
          properties: {
            geminiRoles: { type: "ARRAY", items: { type: "OBJECT", properties: { title: { type: "STRING" }, description: { type: "STRING" }, features: { type: "ARRAY", items: { type: "STRING" } } }, required: ["title", "description", "features"]}},
            workflowSteps: { type: "ARRAY", items: { type: "OBJECT", properties: { number: { type: "INTEGER" }, title: { type: "STRING" }, assignee: { type: "STRING" }, description: { type: "STRING" } }, required: ["number", "title", "assignee", "description"]}},
          },
          required: ["geminiRoles", "workflowSteps"],
        },
      },
    };

    try {
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      if (!response.ok) throw new Error(`API ÏöîÏ≤≠ Ïã§Ìå®: ${response.statusText}`);
      const result = await response.json();
      const jsonText = result.candidates?.[0]?.content?.parts?.[0]?.text;
      if (jsonText) {
        setAnalysisResult(JSON.parse(jsonText));
      } else {
        throw new Error("API ÏùëÎãµÏóêÏÑú Ïú†Ìö®Ìïú Îç∞Ïù¥ÌÑ∞Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");
      }
    } catch (err) {
      console.error(err);
      setError("ÌîÑÎ°úÏÑ∏Ïä§ Î∂ÑÏÑù Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    } finally {
      setIsAnalyzing(false);
    }
  };

  const LoadingSpinner = () => (
    <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path></svg>
  );

  return (
    <div className="min-h-screen bg-gray-50 font-sans text-gray-800 p-4 sm:p-6 lg:p-8">
      <div className="max-w-7xl mx-auto">
        <header className="text-center mb-10">
          <h1 className="text-4xl font-extrabold text-gray-900 mb-2">ÏóÖÎ¨¥ ÌîÑÎ°úÏÑ∏Ïä§ ÏûêÎèôÌôî Î∂ÑÏÑùÍ∏∞ V2</h1>
          <p className="text-lg text-gray-600">AIÎ•º ÌôúÏö©Ìïú ÏõåÌÅ¨ÌîåÎ°úÏö∞ ÏÑ§Í≥Ñ Î∞è ÏµúÏ†ÅÌôî</p>
        </header>

        <div className="max-w-3xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-8 items-start mb-12">
          {/* 1Îã®Í≥Ñ: ÏóÖÎ¨¥ ÏûÖÎ†• */}
          <div className="bg-white p-8 rounded-2xl shadow-md border border-gray-200">
            <h2 className="text-xl font-bold text-gray-800 mb-2">1Îã®Í≥Ñ: ÏóÖÎ¨¥ ÏûÖÎ†•</h2>
            <p className="text-gray-600 mb-4 text-sm">Î∂ÑÏÑùÌïòÍ≥† Ïã∂ÏùÄ ÏóÖÎ¨¥Î•º Í∞ÑÎûµÌïòÍ≤å ÏûÖÎ†•ÌïòÏÑ∏Ïöî.</p>
            <input
              type="text"
              className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500"
              placeholder="Ïòà: Ïã†ÏûÖÏÇ¨Ïõê Ïò®Î≥¥Îî©"
              value={taskInput}
              onChange={(e) => setTaskInput(e.target.value)}
              disabled={isGenerating}
            />
            <button
              onClick={handleGenerateProcess}
              disabled={isGenerating}
              className="mt-4 w-full bg-gray-800 text-white font-bold py-3 px-6 rounded-lg hover:bg-black focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500 disabled:bg-gray-400 flex items-center justify-center"
            >
              {isGenerating ? <><LoadingSpinner /> ÏÉùÏÑ± Ï§ë...</> : "ÌîÑÎ°úÏÑ∏Ïä§ ÏûêÎèô ÏÉùÏÑ±"}
            </button>
          </div>

          {/* 2Îã®Í≥Ñ: ÌîÑÎ°úÏÑ∏Ïä§ ÌôïÏù∏ Î∞è Î∂ÑÏÑù */}
          <div className="bg-white p-8 rounded-2xl shadow-md border border-gray-200">
            <h2 className="text-xl font-bold text-gray-800 mb-2">2Îã®Í≥Ñ: ÌîÑÎ°úÏÑ∏Ïä§ Î∂ÑÏÑù</h2>
            <p className="text-gray-600 mb-4 text-sm">ÏÉùÏÑ±Îêú ÌîÑÎ°úÏÑ∏Ïä§Î•º ÌôïÏù∏, ÏàòÏ†ïÌïòÍ≥† Î∂ÑÏÑùÌïòÏÑ∏Ïöî.</p>
